import { Component} from "react";
import Square from './Square'
class Board extends Component{
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill('a'),
    }
  }
  render(){
    let { squares } = this.state
    let squareContainer = [[],[],[]]
    squares.forEach((item,index)=>{
      let rowIndex = parseInt(index/3)
      squareContainer[rowIndex].push(item)
    })
    let squareAll = []
    squareContainer.forEach((item,index)=>{
      let squareRow = []
      item.forEach((ele,idx)=>{
        squareRow.push(<Square value={ele} key={`${index}-${idx}`}></Square>)
      })
      squareAll.push(<div key={index}>{squareRow}</div>)
    })
    return (
      <div>
        <p>next player is A</p>
        <div>
          {squareAll}
        </div>
      </div>
    )
  }
}
export default Board
