import { Component} from "react";
import Square from './Square'
class Board extends Component{
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(null),
    }
  }
  handleSquare(row=0,col=0){
    let { squares } = this.state
    squares[3*row + col ] = 'X'
  }
  render(){
    let { squares } = this.state
    let squareContainer = [[],[],[]]
    squares.forEach((item,index)=>{
      let rowIndex = parseInt(index/3)
      squareContainer[rowIndex].push(item)
    })
    let squareAll = []
    squareContainer.forEach((item,index)=>{
      let squareRow = []
      item.forEach((ele,idx)=>{
        squareRow.push(<Square value={ele} key={`${index}-${idx}`} row={index} col={idx} handleSquar={this.handleSquare}></Square>)
      })
      squareAll.push(<div key={index} className={'square_row'}>{squareRow}</div>)
    })
    return (
      <div className={'square_board'}>
        <p className={'player_note'}>next player is A</p>
        <div>
          {squareAll}
        </div>
        <ul className={'operation_list'}>
          <li>step:1</li>
          <li>step:2</li>
          <li>step:3</li>
        </ul>
      </div>
    )
  }
}
export default Board
