import { Component } from 'react'

function Square(props) {
  return (
    <button className='square' onClick={()=>{alert(props.value)}} >{props.value}</button>
  )
}

class Board extends Component {
  constructor(props){
    super(props)
    this.state={
      squares:Array(9).fill(null)
    }
  }
  render() {
    let squareAll = []

    let borderAll = [[],[],[]]

    return (
      <div className='board'>
        <div>Next Player:X</div>
        <div>{squareAll}</div>
      </div>
    )
  }
}

class Game extends Component {
  render() {
    return (
      <div className={'game'}>
        <Board></Board>
        <div className="gameInfo">
          <ol>
            <li>step:1</li>
            <li>step:2</li>
            <li>step:3</li>
          </ol>
        </div>
      </div>
    )
  }
}

class Index extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <Game></Game>
    )
  }
}

export default Index
