import { Component} from "react";
import Square from './Square'
class Board extends Component{
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(null),
    }
  }
  render(){
    let { squares } = this.state
    let squareContainer = [[],[],[]]
    squares.forEach((item,index)=>{
      let rowIndex = parseInt(index/3)
      squareContainer[rowIndex].push(squares)
    })

    let squareAll = []
    squareContainer.forEach((ele,idx)=>{
      let squareRow = []
      ele.forEach((eleItem,eleIndex)=>{
        squareRow.push(<Square value={eleItem} key={eleItem + eleIndex}></Square>)
      })
      squareAll.push(<div>{squareRow}</div>)
    })


    return (
      <div>
        <p>next player is A</p>
        <div>
          {squareAll}
        </div>
      </div>
    )
  }
}
export default Board
